const add = function(a,b) {
	return a+b;
};

const subtract = function(a,b) {
	return a-b;
};

const sum = function(a) {
  let sum = 0;
  if (a.length = 0){
    return sum;
  }else {
  
  for (i=0; i< a.length; i++){
    sum = sum + a[i];

  }}
  return sum;
	
};

